import React, { useState } from 'react';
import { useHistory } from 'react-router';
import {
    Form,
    FormGroup,
    Button
} from 'reactstrap'; 
import "../../styles/Register.css"

const JobSignUp = ({ JobSignUp, currentUser }) => {
    const INITIAL_DATA = {
        position: "",
        hourlyPay: "", 
        date: ""
    };

    const [formData, setFormData] = useState(INITIAL_DATA);
    const [errors, setErrors] = useState([]);
    let history = useHistory();

    const handleSubmit = async e => {
        e.preventDefault();
        const registerData = {
            "position": formData.position,
            "hourlyPay":formData.hourlyPay,
            "date": formData.date,
            "companyId": currentUser           
        };
        const result = await JobSignUp(registerData)
        setFormData(INITIAL_DATA);

        if (result.success) {
            alert("Added new job")
            history.push("/");
        } else {
            setErrors(result.errors);
        }
    }

    const handleChange = e => {
        const {name, value} = e.target;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div>
            <h1>Signup a Company</h1>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <label id="position">Position: </label>
                    <input
                        type="text"
                        name="position"
                        onChange={handleChange}
                        value={formData.position}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="hourlyPay">Hourly Pay: </label>
                    <input
                        type="text"
                        name="hourlyPay"
                        onChange={handleChange}
                        value={formData.hourlyPay}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="date">Date: </label>
                    <input
                        type="date"
                        name="address"
                        onChange={handleChange}
                        value={formData.address}
                    />
                </FormGroup>
                <Button>Submit</Button>
                <div>
                    {errors.length ?
                        <p>{errors}</p>
                    : null }
                </div>
            </Form>
        </div>
    )
}

export default JobSignUp;
