import React, { useState, useEffect } from 'react';
import { getJobsForCompany } from '../utilities/utility';
import JobCard from "./JobCard";
import '../../styles/CompanyCard.css';

const CompanyCard = ({ company, setClickedCompany, clickedCompany }) => {
    const [jobs, setJobs] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const { id, name, url, address } = company;

    useEffect(() => {
        async function getJobs() {
            let jobList = await getJobsForCompany(company.id);
            if (jobList.success) {
                setJobs(jobList.jobs);
            } 
            setIsLoading(false);
        }
        getJobs();
    }, [company.id]);

    const setCompany = () => {
        setClickedCompany(company)
    }

    if (isLoading) {
        return <p>Loading</p>
    }

    return (
        <div className="company-card">
            <div onClick={setCompany} className={clickedCompany.name === name ? "selected-card" : "inner-card"}>
                <h1>{id}. {name}</h1>
                <h3>{address}</h3>
                <a href={url} target="blank">{url}</a>
                <h5>Jobs</h5>
                {jobs.length > 0 ? jobs.map(job => (
                    <JobCard key={job.id} job={job}/>
                )) : <p>No jobs to display</p>}
            </div>
        </div> 
    )
}

export default CompanyCard;
