import React, { useState } from 'react';
import { getJobsForCompany } from '../utilities/utility';
import JobCard from "./JobCard";
import '../../styles/CompanyCard.css';

const CompanyCard = ({ company, setClickedCompany, clickedCompany }) => {
    const [jobs, setJobs] = useState([]);
    const { name, url, address } = company;
    console.log(clickedCompany)

    async function toggleJobs() {
        if (Object.keys(clickedCompany).length === 0) {
            setClickedCompany(company);
            const jobList = await getJobsForCompany(company.id)
            if (jobList.success) {
                setJobs(jobList.jobs)
                console.log(jobList.jobs)
            }
        } else {
            setClickedCompany({});
        }
    }

    return (
        <div className="company-card">
            <div className={clickedCompany.name === name ? "selected-card" : "inner-card"}>
                <h1>{name}</h1>
                <h3>{address}</h3>
                <a href={url} target="blank">{url}</a><br></br>
                <button className="btn btn-dark btn-sm" onClick={toggleJobs}>View Open Jobs</button>
                {jobs.length > 0 ?
                jobs.map(job => (
                    <JobCard key={job.id} job={job}/>
                ))
                : null}
            </div>
        </div> 
    )
}

export default CompanyCard;
