import React from 'react';
import '../../styles/CompanyCard.css';

const CompanyCard = ({company, setClickedCompany, clickedCompany}) => {

    const {name, url, address,latitude, longitude} = company;
    const directions = `https://www.google.com/maps/dir/?api=1&travelmode=driving&layer=traffic&destination=${latitude},${longitude}`;

    function mapCompany() {
        setClickedCompany(company);
    }

    return (
        <div className="company-card">
            <div className={clickedCompany.name === name ? "selected-card" : "inner-card"}>
                <h1>{name}</h1>
                <h3>{address}</h3>
                <a href={url} target="blank">{url}</a><br></br>
                <a href={directions} rel="noreferrer" target="_blank" className="btn btn-dark btn-sm">Directions</a>
                <button className="btn btn-dark btn-sm" onClick={mapCompany}>See on Map</button>
            </div>
        </div> 
    )
}

export default CompanyCard;
