const ChefApi = import('../api/api');

const registerJob = async formData => {
    try {
        await ChefApi.signUpJob(formData)
        return {success: true}
    } catch (err) {
        return {success: false, errors: err}
    }
}

const companySignUp = async formData => {
    try {
        await ChefApi.signUpCompany(formData)
        return {success: true}
    } catch (err) {
        return {success: false, errors: err}
    }
}


module.exports = {
    CompanySignUp,
    registerJob
}
