import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import logo from "../../assets/sub chef LOGO/Artboard 2.png";
import AppBar from '@material-ui/core/AppBar';
import MenuIcon from '@material-ui/icons/Menu';
import AccountCircle from '@material-ui/icons/AccountCircle';
import { Menu, 
        Button,
        MenuItem,
        IconButton,
        Typography,
        Toolbar } from '@material-ui/core';
import '../../styles/Navbar.css';
import useMediaQuery from '@material-ui/core/usemediaquery';
import { withRouter } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    [theme.breakpoints.down("xs")]: {
        flexGrow: 1,
    }
  },
  headerOptions: {
      display: "flex",
      flex: 1,
      justifyContent: "space-evenly"
  }
}));

const Header = (props, { LogOut }) => {
  const { history } = props;
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("xs"));
  const token = localStorage.getItem('token');

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClick = (pageURL) => {
    history.push(pageURL);
    setAnchorEl(null);
  };

  const handleButtonClick = pageURL => {
    history.push(pageURL);
  }

  return (
    <div className={classes.root}>
      <AppBar position="fixed" color="light">
        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            <img className="logo" src={logo} alt="logo"/>
          </Typography>
          {isMobile ? 
          <>
            <div>
              <IconButton
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleMenu}
                color="inherit"
              >
                <AccountCircle />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={open}
                onClose={() => setAnchorEl(null)}
              >
                <MenuItem onClick={() => handleMenuClick('/user')}>My Account</MenuItem>
                <MenuItem onClick={() => handleMenuClick('/companies')}>Company</MenuItem>
                <MenuItem onClick={() => LogOut}>LogOut</MenuItem>
              </Menu>
            </div>
            </>
              : (
                  <div className={classes.headerOptions}>
                      <Button variant="contained" onClick= {() => handleButtonClick('/')}>Home</Button>
                      <Button variant="contained" onClick= {() => handleButtonClick('/user')}>My Account</Button>
                      <Button variant="contained" onClick= {() => handleButtonClick('/companies')}>Company</Button>
                      <Button variant="contained" onClick= {() => handleButtonClick('/')}>Logout</Button>
                  </div>
              )
            }
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default withRouter(Header);
