import React, { useState, useContext, useEffect } from 'react';
import { editUser } from '../utilities/utility';
import UserContext from '../routes/UserContext';
import ChefApi from '../api/api';
import { useHistory } from 'react-router';
import {
    TextField,
    Button,
    ButtonGroup } from '@material-ui/core';
import Box from '@material-ui/core/Box';
import DeleteIcon from '@material-ui/icons/Delete';
import SaveIcon from '@material-ui/icons/Save';
const UserProfile = ({ setUserInfo, LogOut }) => {

    const { userInfo } = useContext(UserContext);
    const [formData, setFormData] = useState(null);
    const [errors, setErrors] = useState([]);
    let history = useHistory();

    useEffect(() => {
        if (userInfo !== null) {
            setFormData({
                firstName: userInfo.firstName,
                lastName: userInfo.lastName,
                email: userInfo.email
            });
        }
    }, [userInfo])

    const handleSubmit = async e => {
    e.preventDefault();
    const editData = {
        "firstName": formData.firstName,
        "lastName": formData.lastName,
        "email": formData.email
    };
    await editUser(userInfo.username, editData)
        .then((res) => {
            if (res.success) {
                const NEW_STATE = {
                    "firstName": res.user.firstName,
                    "lastName": res.user.lastName,
                    "email": res.user.email
                }
                setUserInfo(data => ({
                    ...data,
                    ...NEW_STATE
                }))
                alert("Updated user info")
            }
            else {
                setErrors(res.errors);
            }
        })
    }

    const deleteUser = async () => {
        try {
            const user = await ChefApi.deleteUser(userInfo.username)
            console.log(user)
            alert(`Deleted ${user.deleted}. Sorry to see you go!`)
            LogOut()
            history.push('/')
        }
        catch (err) {
            console.log(err)
            setErrors(`Unable to delete ${userInfo.username}`)
        }
    }

    const handleChange = e => {
        const name = e.target.name;
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div className="border mt-3">
        {formData === null  ? <p>Loading...</p> :
            <div>
                <h1>Edit User Profile</h1>
                <form className="user-profile white" onSubmit={handleSubmit}>
                    <TextField
                        label="First Name"
                        type="text"
                        name="firstName"
                        onChange={handleChange}
                        value={formData.firstName}/>
                        <br></br>
                    <TextField
                        label="Last Name"
                        type="text"
                        name="lastName"
                        onChange={handleChange}
                        value={formData.lastName}/>
                        <br></br>
                    <TextField
                        label="Email"
                        type="email"
                        name="email"
                        onChange={handleChange}
                        value={formData.email}/>
                    <Box m={2} p={3}>
                        <ButtonGroup size="small" aria-label="small outlined button group">
                            <Button 
                                type="submit"
                                variant="contained" 
                                color="secondary"
                                className="btn mr-2"
                                startIcon={<SaveIcon />}>
                                Edit Account
                            </Button>                    
                            <Button 
                                onClick={deleteUser}
                                variant="contained" 
                                color="primary"
                                startIcon={<DeleteIcon />}>
                                Delete Account
                            </Button>
                        </ButtonGroup>
                    </Box>
                </form>
                <div>
                    {errors.length ?
                        <p>{errors}</p>
                        : null }
                </div>
            </div>
        }
        </div>
    )
}

export default UserProfile;
