import React from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
require('dotenv').config()

const Map = ({clickedCompany}) => {
    const location = {lat: clickedCompany.latitude, lng: clickedCompany.longitude}
    const mapStyles = {
        height: "70vh",
        width: "100%"
    }

    return (
        Object.keys(clickedCompany).length !== 0  ?
            <LoadScript
                googleMapsApiKey= {process.env.REACT_APP_API_KEY}>
            <GoogleMap
                mapContainerStyle = { mapStyles }
                zoom = { 13 }
                center = { location } 
            >
            {
                <Marker 
                    key={clickedCompany.name} 
                    position={location} />
            })
            </GoogleMap >
            </LoadScript>
            :
            <div className="mobile-empty">Click a Company card to load the map</div>
    )
}

export default Map;
