import React, { useState, useEffect } from 'react';
import ChefApi from '../api/api';
import Button from '@material-ui/core/Button';
import EditCompany from './EditCompany';

const CompaniesProfile = ({ currentUser }) => {
    const [companies, setCompanies] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function getCompanies() {
            const userCompanies = await ChefApi.getCompaniesFromUsername(currentUser);
            setCompanies(userCompanies.companies);
            setIsLoading(false);
        } getCompanies()
    }, [companies, currentUser])

    const editCompany = (company) => {
        return <EditCompany company={company}/>
    }

    return (
        isLoading ? <div>Loading...</div> :
        companies.map(company => (
            <div key={company.id}>
                <h1>{company.name}</h1>
                <h3>{company.address}</h3>
                <h3>{company.url}</h3>
                    <Button onClick={editCompany(company)} variant="contained" color="primary">Edit Company</Button>
            </div>
        ))
    )
}

export default CompaniesProfile;
