import React, { useState, useContext, useEffect } from 'react';
import { editUser } from '../utilities/utility';
import UserContext from '../routes/UserContext';
import {
    Form,
    FormGroup,
    Button
} from 'reactstrap'; 

const UserProfile = ({ setUserInfo }) => {

    const { userInfo } = useContext(UserContext);

    const [formData, setFormData] = useState(null);
    const [errors, setErrors] = useState([]);

    useEffect(() => {
        if (userInfo !== null) {
            setFormData({
                firstName: userInfo.firstName,
                lastName: userInfo.lastName,
                email: userInfo.email,
                isCompany: userInfo.isCompany
            });
        }
    }, [userInfo])

    const handleSubmit = async e => {
    e.preventDefault();
    const editData = {
        "firstName": formData.firstName,
        "lastName": formData.lastName,
        "email": formData.email,
        "isCompany": formData.isCompany            
    };
    await editUser(userInfo.username, editData)
        .then((res) => {
            if (res.success) {
                console.log('res', res)
                const NEW_STATE = {
                    "firstName": res.user.firstName,
                    "lastName": res.user.lastName,
                    "email": res.user.email,
                    "isCompany": res.user.isCompany
                }
                setUserInfo(data => ({
                    ...data,
                    ...NEW_STATE
                }))
                alert("Updated user info")
            }
            else {
                setErrors(res.errors);
            }
        })
    }

    const handleChange = e => {
        const name = e.target.name;
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <>
            <h1>User Profile</h1>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <label id="firstName">First Name: </label>
                    <input
                        type="text"
                        name="firstName"
                        onChange={handleChange}
                        value={formData.firstName}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="lastName">Last Name: </label>
                    <input
                        type="text"
                        name="lastName"
                        onChange={handleChange}
                        value={formData.lastName}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="email">Email:</label>
                    <input
                        type="email"
                        name="email"
                        onChange={handleChange}
                        value={formData.email}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="isCompany">Are you a Business?: </label>
                    <input
                        type="checkbox"
                        name="isCompany"
                        onChange={handleChange}
                        value={formData.isCompany}
                        checked={!!formData.isCompany}
                    />
                </FormGroup>
                <Button>Edit Account</Button>
                <div>
                    {errors.length ?
                        <p>{errors}</p>
                        : null }
                </div>
            </Form>
            <a href="/applications"><Button>View Applications</Button></a>
            {userInfo.isCompany ?
            <a href="/companies"><Button>View Company Profile</Button></a>
            :
            null }
        </>
    )
}

export default UserProfile;
