import React, { useState, useEffect } from 'react';
import ChefApi from '../api/api';
import { formatDate } from '../utilities/utility'; 

const CompaniesApplications = ({currentUser}) => {
    const [applications, setApplications] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function getApplications() { 
            let applicationsArr = []
            const userCompanies = await ChefApi.getCompaniesFromUsername(currentUser);
            for (let company of userCompanies.companies) {
                const companyApplications = await ChefApi.getJobsForCompany(company.id)
                applicationsArr.push([company, companyApplications])
            }
            setApplications(applicationsArr);
            console.log(applications)
            setIsLoading(false); 
        }
        if (isLoading && currentUser !== null) {
            getApplications()
        }
    }, [applications, currentUser, isLoading])


    return (
        applications.length > 0 ?
        <div>
            <h1>Current Applications</h1>
            {!isLoading ? <div>Loading...</div> : null}
            <ul className="applications-ul">
                {applications.map(application => (
                    <div key={application[0].id}>
                        <li>Company: {application[0].name}</li>
                        <li>Address: {application.company.address}</li>
                        <li>URL: {application.company.url}</li>
                        <br></br>
                        <li>Position: {application.job.position}</li>
                        <li>Hourly Pay: {application.job.hourly_pay}</li>
                        <li>Date: {formatDate(application.job.date)}</li>
                        <li>Status: {application.status}</li><br></br>
                    </div>
                ))}
            </ul>
        </div>
        : 
        <h1>Apply to jobs to view applications</h1>
    )
}

export default CompaniesApplications;
