import React from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';
require('dotenv').config()

const Map = ({companies}) => {
    console.log(companies)

    const getCoordinates = async () => {
        for (let company of companies) {
            const location = company.address
            await axios.get('https://maps.googleapis.com/maps/api/geocode/json', {
                params:{
                    address: location,
                    key: process.env.REACT_APP_API_KEY
                }
            })
            .then(function(response){
                let coords = response.data.results[0].geometry.location.lat
                let lng = response.data.results[0].geometry.location.lng

                console.log(lat, lng)
            })
            .catch(function(error) {
                console.log(error)
            }) 

        }
    }
    getCoordinates()
    const location = {lat: -20, lng: 144}
    const mapStyles = {
        height: "70vh",
        width: "100%"
    }

    return (
        Object.keys(companies).length !== 0  ?
            <LoadScript
                googleMapsApiKey= {process.env.REACT_APP_API_KEY}>
            <GoogleMap
                mapContainerStyle = { mapStyles }
                zoom = { 13 }
                center = { location } 
            >
            {
                <Marker 
                    key={companies[0].name} 
                    position={location} />
            })
            </GoogleMap >
            </LoadScript>
            :
            <div className="mobile-empty">Click a Company card to load the map</div>
    )
}

export default Map;
