import React, { useState } from 'react';
import CompanyCard from './CompanyCard';
import testData from './RestaurantData';
import ChefApi from '../api/api';
import Map from './Map';

const CompanyList = () => {

    const [companies, setCompanies] = useState([]);
    const [clickedCompany, setClickedCompany] = useState({});
    const [isLoaded, setIsLoaded] = useState(false);

    const getCompanyInfo = async () => {
        try {
            await ChefApi.getAllCompanies()
                .then(results => setCompanies(results))
            setIsLoaded(true)
        }
        catch (err) {
            console.log(err);
        }
    }

    return (
        <div className="container-fluid">
            <div className="row">
                {!isLoaded ? <div>Loading...</div> : null}
                {isLoaded && companies.length > 0 ?
                companies.map(company => (
                    <CompanyCard
                        key={company.id}
                        company={company}
                        clickedCompany={clickedCompany}
                        setClickedCompany={setClickedCompany}
                    />
                )) 
                : getCompanyInfo()
                }
            </div>
            <div className="row">
                <Map 
                    clickedCompany={clickedCompany}
                />
            </div>
        </div>
    )
}

export default CompanyList;
