import React, { useState } from 'react';
import { useHistory } from 'react-router';
import {
    Form,
    FormGroup,
    Button
} from 'reactstrap'; 
import "../../styles/Register.css"

const Signup = ({SignUp}) => {
    const INITIAL_DATA = {
        username: "",
        password: "", 
        firstName: "", 
        lastName: "", 
        email: "",
        isCompany: ""
    };

    const [formData, setFormData] = useState(INITIAL_DATA);
    const [errors, setErrors] = useState([]);
    let history = useHistory();

    const handleSubmit = async e => {
        e.preventDefault();
        const registerData = {
            "username": formData.username,
            "password":formData.password,
            "firstName": formData.firstName,
            "lastName": formData.lastName,
            "email": formData.email,
            "isCompany": formData.isCompany            
        };
        const result = await SignUp(registerData)
        if (formData.isCompany) {
            const companyRedirect = true;
        }
        setFormData(INITIAL_DATA);

        if (result.success) {
            alert("Created account for Substitute Chef")
            if (companyRedirect) {
                history.push("/company")
            }
            else {
                history.push("/");
            }
        } else {
            setErrors(result.errors);
        }
    }

    const handleChange = e => {
        const name = e.target.name;
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        console.log(name, value)
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div className="register-div">
            <h1>Signup for Substitute Chef</h1>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <label id="username">Username: </label>
                    <input
                        type="text"
                        name="username"
                        onChange={handleChange}
                        value={formData.username}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="password">Password: </label>
                    <input
                        type="password"
                        name="password"
                        onChange={handleChange}
                        value={formData.password}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="firstName">First Name: </label>
                    <input
                        type="text"
                        name="firstName"
                        onChange={handleChange}
                        value={formData.firstName}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="lastName">Last Name: </label>
                    <input
                        type="text"
                        name="lastName"
                        onChange={handleChange}
                        value={formData.lastName}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="email">Email:</label>
                    <input
                        type="email"
                        name="email"
                        onChange={handleChange}
                        value={formData.email}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="isCompany">Are you a Business?: </label>
                    <input
                        type="checkbox"
                        name="isCompany"
                        onChange={handleChange}
                        value={formData.isCompany}
                    />
                </FormGroup>
                <Button>Submit</Button>
                <div>
                    {errors.length ?
                        <p>{errors}</p>
                    : null }
                </div>
            </Form>
        </div>
    )
}

export default Signup;
