import ChefApi from '../api/api';

const Companies = ({ currentUser }) => {
    const [companies, setCompanies] = useState(null);
    const [isLoading, setIsLoading] = useState(null);

    useEffect(() => {
        async function getCompanies() {
            const userCompanies = await ChefApi.getCompaniesFromUsername(currentUser);
            setCompanies(userCompanies.companies);
            setIsLoading(true);
            console.log(userCompanies)
        } getCompanies()
    }, [companies, currentUser])

    console.log(companies)
    return (

        companies.map(company => (
            <div key={company.id}>
                <h1>{company.name}</h1>
                <h3>{company.address}</h3>
                <h3>{company.url}</h3>
                <a href="/editcompany"><button>Edit company</button></a>
            </div>
        ))
    )
}

export default Companies;
