import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import logo from "../../assets/sub chef LOGO/Artboard 2.png";
import AppBar from '@material-ui/core/AppBar';
import AccountCircle from '@material-ui/icons/AccountCircle';
import { Menu, 
        List,
        Collapse,
        MenuItem,
        IconButton,
        Typography,
        Toolbar } from '@material-ui/core';
import { ExpandMore,
         ExpandLess } from '@material-ui/icons';
import { withRouter } from 'react-router-dom';
import '../../styles/Navbar.css';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    flexGrow: 1,
  },
  nested: {
    paddingLeft: theme.spacing(4)
  },
  title: {
        flexGrow: 1,
  },
  headerOptions: {
      display: "flex",
      flex: 1,
      justifyContent: "space-evenly"
  }
}));

const Header = (props) => {
  const { history } = props;
  const { LogOut } = props;
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const token = localStorage.getItem('token');

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClick = (pageURL) => {
    history.push(pageURL);
    setAnchorEl(null);
  };

  const loggedOutMenuItems = [
      {
          menuTitle: 'Log in',
          pageURL: '/login'
      },
      {
          menuTitle: 'Sign up',
          pageURL: '/signup'
      }
  ]

    const [openList, setOpenList] = useState(false);
    const handleClick = () => {
      setOpenList(!openList);
    };
    const [companyOpenList, setCompanyOpenList] = useState(false);
    const handleCompanyClick = () => {
      setCompanyOpenList(!companyOpenList);
    };


  return (
    <div className={classes.root}>
      <AppBar className="mb-4" position="static" color="transparent">
        <Toolbar>
          <Typography edge="start" className={classes.menuButton} color="inherit">
            <a href="/"><img className="logo" src={logo} alt="logo"/></a>
          </Typography>
          <Typography variant="h6" className={classes.title}>
              Find Jobs, Get Paid
          </Typography>
            <div>
              <IconButton
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleMenu}
                color="inherit"
              >
                <AccountCircle />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={open}
                onClose={() => setAnchorEl(null)}
              >
            {token ? 
            <div>
                <MenuItem button onClick={handleClick}>User
                  {openList ? <ExpandLess /> : <ExpandMore />}
                  <Collapse in={openList} timeout="auto" unmountOnExit>
                    <List component="div" disablePadding>
                    <MenuItem button
                      className={classes.nested}
                      key={1}
                      primaryText="Profile"
                      onClick={() => handleMenuClick('/userprofile')}>Profile</MenuItem>
                    <MenuItem button
                      className={classes.nested}
                      key={2}
                      primaryText="Applications"
                      onClick={() => handleMenuClick('/userapplications')}>Applications</MenuItem>
                    </List>
                  </Collapse>
                </MenuItem>
                <MenuItem button onClick={handleCompanyClick}>Companies
                  {companyOpenList ? <ExpandLess /> : <ExpandMore />}
                  <Collapse in={companyOpenList} timeout="auto" unmountOnExit>
                    <MenuItem button
                      key={1}
                      className={classes.nested}
                      onClick={() => handleMenuClick('/companyprofile')}>Profile</MenuItem>
                    <MenuItem
                      key={2}
                      className={classes.nested}
                      onClick={() => handleMenuClick('/companiesapplications')}>Applications</MenuItem>
                    <MenuItem
                      key={3}
                      className={classes.nested}
                      onClick={() => handleMenuClick('/company')}>Create New Company</MenuItem>
                    <MenuItem
                      key={4}
                      className={classes.nested}
                      onClick={() => handleMenuClick('/job')}>Post a Job</MenuItem>
                  </Collapse>
                </MenuItem>
                <MenuItem onClick={() => LogOut()}>Logout</MenuItem>
            </div>        
                : loggedOutMenuItems.map(menuItem => {
                    const { menuTitle, pageURL } = menuItem;
                    return (
                        <MenuItem onClick={() => handleMenuClick(pageURL)}>{menuTitle}</MenuItem>
                        )
                })
            }
              </Menu>
            </div>
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default withRouter(Header);
