import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router';
import { registerJob, getCompaniesFromUsername } from '../utilities/utility';
import {
    Form,
    FormGroup,
    Button
} from 'reactstrap'; 
import "../../styles/Register.css"
import ChefApi from '../api/api';

const JobSignUp = ({ currentUser }) => {
    const [companies, setCompanies] = useState([]);

    useEffect(() => {
        async function fetchCompanies() {
            const userCompanies = await ChefApi.getCompaniesFromUsername(currentUser)
            setCompanies(userCompanies)
            console.log(companies)
        }
        fetchCompanies()
    }, [currentUser])
    const INITIAL_DATA = {
        position: "",
        hourlyPay: "", 
        date: ""
    };

    const [formData, setFormData] = useState(INITIAL_DATA);
    const [errors, setErrors] = useState([]);
    let history = useHistory();

    const handleSubmit = async e => {
        e.preventDefault();
        const registerData = {
            "position": formData.position,
            "hourlyPay":formData.hourlyPay,
            "date": formData.date,
            "companyId": formData.companyId         
        };
        const result = await registerJob(registerData)
        setFormData(INITIAL_DATA);

        if (result.success) {
            alert("Added new job")
            history.push("/");
        } else {
            setErrors(result.errors);
        }
    }

    const handleChange = e => {
        const {name, value} = e.target;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div>
            <h1>Post a Job</h1>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <label id="position">Position: </label>
                    <input
                        type="text"
                        name="position"
                        onChange={handleChange}
                        value={formData.position}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="hourlyPay">Hourly Pay: </label>
                    <input
                        type="number"
                        min="14"
                        name="hourlyPay"
                        onChange={handleChange}
                        value={formData.hourlyPay}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="date">Date: </label>
                    <input
                        type="date"
                        name="date"
                        onChange={handleChange}
                        value={formData.date}
                    />
                </FormGroup>
                <FormGroup>
                    <label id="company">Company: </label>
                    <select value={formData.company} onChange={handleChange}>
                        {/* {companies.length > 0 ? 
                            companies.map(company => (
                            <option value={company.name}>{company.name}</option>
                        )):  */}
                        <option value="grapefruit">Grapefruit</option>
                        <option value="lime">Lime</option>
                        <option value="coconut">Coconut</option>
                        <option value="mango">Mango</option> 

                    </select>
                </FormGroup>
                <Button>Submit</Button>
                <div>
                    {errors.length ?
                        <p>{errors}</p>
                    : null }
                </div>
            </Form>
        </div>
    )
}

export default JobSignUp;
