import React, { useEffect, useState } from 'react';
import { Button } from '@material-ui/core'
import {
    Form,
    FormGroup
} from 'reactstrap'; 

const EditCompany = ({ company }) => {
    const { id, name, address, url } = company
    const [formData, setFormData] = useState(null);
    const [errors, setErrors] = useState([]);

    useEffect(() => {
        if (company !== null) {
            setFormData({
                name: name,
                url: url,
                address: address,
            });
        }
    }, [company])


    const handleSubmit = async e => {
    e.preventDefault();
    const editData = {
        "name": formData.name,
        "address": formData.address,
        "url": formData.url,
    };
    await editCompany(id, editData)
        .then((res) => {
            if (res.success) {
                console.log('res', res)
                const NEW_STATE = {
                    "name": res.company.name,
                    "address": res.company.address,
                    "url": res.company.url
                }
                setFormData(data => ({
                    ...data,
                    ...NEW_STATE
                }))
                alert("Updated company info")
            }
            else {
                setErrors(res.errors);
            }
        })
    }

    const handleChange = e => {
        const { name, value } = e.target;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div>
        {formData === null  ? <p>Loading...</p> :
            <div>
                <h1>{name}</h1>
                <Form onSubmit={handleSubmit}>
                    <FormGroup>
                        <label id="name">Name: </label>
                        <input
                            type="text"
                            name="name"
                            onChange={handleChange}
                            value={formData.name}
                        />
                    </FormGroup>
                    <FormGroup>
                        <label id="url">URL: </label>
                        <input
                            type="text"
                            name="url"
                            onChange={handleChange}
                            value={formData.url}
                        />
                    </FormGroup>
                    <FormGroup>
                        <label id="address">address:</label>
                        <input
                            type="text"
                            name="address"
                            onChange={handleChange}
                            value={formData.address}
                        />
                    </FormGroup>
                    <Button variant="contained" color="primary">Edit Company</Button>
                    <div>
                        {errors.length ?
                            <p>{errors}</p>
                            : null }
                    </div>
                </Form>
                <a href="/companyapplications"><Button>View Applications</Button></a>
            </div>
        }

            <h1>{name}</h1>
            <h3>{address}</h3>
            <h3>{url}</h3>
            <Button variant="contained" color="primary">Edit Company</Button>
        </div>

    )
}

export default EditCompany;
