import React, { useContext, useState } from 'react';
import {
    Button
} from 'reactstrap'; 
import UserContext from '../routes/UserContext';
import ChefApi from '../api/api';
import { formatDate, getJobsForCompany } from '../utilities/utility';

const JobCard = ({ job }) => {
    const { currentUser } = useContext(UserContext);
    const [applied, setApplied] = useState(false);

const applyToJob = async () => {
    try {
        const applications = await getJobs();
        const jobId = await ChefApi.applyToJob(currentUser, job.id)
        alert(`Applied to ${job.position} job`)
        return {success: true, jobId}
    } catch (err) {
        console.log(err)
        return {success: false, errors: err}
    }
}

    return (
        <div>
            <p>Position: {job.position}</p>
            <p>Hourly pay: ${job.hourly_pay}</p>
            <p>Date: {formatDate(job.date)}</p>
            <Button onClick={applyToJob} className="btn btn-success btn-sm">Apply</Button>
        </div> 
    )
}

export default JobCard;
