import React, { useState, useEffect } from 'react';
import ChefApi from '../api/api';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';

const CompaniesProfile = ({ currentUser }) => {
    const [companies, setCompanies] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function getCompanies() {
            const userCompanies = await ChefApi.getCompaniesFromUsername(currentUser);
            setCompanies(userCompanies.companies);
            setIsLoading(false);
        } getCompanies()
    }, [companies, currentUser])


    return (
        isLoading ? <div>Loading...</div> :
        companies.map(company => (
            <div key={company.id}>
                <h1>{company.name}</h1>
                <h3>{company.address}</h3>
                <h3>{company.url}</h3>
                <Link to={`/editcompany/${company.id}`}>                    
                    <Button variant="contained" color="primary">Edit Company</Button>
                </Link>
            <Link to={`route/${company.id}`}>My route</Link>
            </div>
        ))
    )
}

export default CompaniesProfile;
