import React, { useState, useContext, useEffect } from 'react';
import { editUser } from '../utilities/utility';
import UserContext from '../routes/UserContext';
import {
    TextField,
    Checkbox,
    FormControlLabel,
    Button
} from '@material-ui/core';

const UserProfile = ({ setUserInfo }) => {

    const { userInfo } = useContext(UserContext);

    const [formData, setFormData] = useState(null);
    const [errors, setErrors] = useState([]);

    useEffect(() => {
        if (userInfo !== null) {
            setFormData({
                firstName: userInfo.firstName,
                lastName: userInfo.lastName,
                email: userInfo.email,
                isCompany: userInfo.isCompany
            });
        }
    }, [userInfo])

    const handleSubmit = async e => {
    e.preventDefault();
    const editData = {
        "firstName": formData.firstName,
        "lastName": formData.lastName,
        "email": formData.email,
        "isCompany": formData.isCompany            
    };
    await editUser(userInfo.username, editData)
        .then((res) => {
            if (res.success) {
                console.log('res', res)
                const NEW_STATE = {
                    "firstName": res.user.firstName,
                    "lastName": res.user.lastName,
                    "email": res.user.email,
                    "isCompany": res.user.isCompany
                }
                setUserInfo(data => ({
                    ...data,
                    ...NEW_STATE
                }))
                alert("Updated user info")
            }
            else {
                setErrors(res.errors);
            }
        })
    }

    const handleChange = e => {
        const name = e.target.name;
        const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div>
        {formData === null  ? <p>Loading...</p> :
            <div>
                <a href="/userapplications"><Button>View Open Applications</Button></a>
                <h1>Edit User Profile</h1>
                <form onSubmit={handleSubmit}>
                    <TextField
                        required
                        label="First Name"
                        type="text"
                        name="firstName"
                        onChange={handleChange}
                        value={formData.firstName}/>
                        <br></br>
                    <TextField
                        required
                        label="Last Name"
                        type="text"
                        name="lastName"
                        onChange={handleChange}
                        value={formData.lastName}/>
                        <br></br>
                    <TextField
                        required
                        label="Email"
                        type="email"
                        name="email"
                        onChange={handleChange}
                        value={formData.email}/>
                        <br></br>
                    <FormControlLabel
                        control={<Checkbox value={formData.isCompany} checked={formData.isCompany} onChange={handleChange} name="isCompany" />}
                        label="Register a Company?"/>
                        <br></br>
                    <Button 
                        type="submit"
                        variant="contained" 
                        color="primary">Edit User</Button>
                        <div>
                            {errors.length ?
                                <p>{errors}</p>
                                : null }
                        </div>
                </form>
            </div>
        }
        </div>
    )
}

export default UserProfile;
