import React, { useState, useEffect } from 'react';
import ChefApi from '../api/api';
import { formatDate } from '../utilities/' 

const Applications = ({ userInfo }) => {
    const [applications, setApplications] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        async function getApplications() {
            const jobApps = [];
            for (let jobId of userInfo.applications) {
                const job = await ChefApi.getJob(jobId);
                jobApps.push(job);
            }
            setApplications(jobApps)
            console.log(applications)
            setIsLoaded(true);
        }
        getApplications();
    }, [userInfo]);

    return (
        <div>
            <h1>Current Applications</h1>
            {!isLoaded ? <div>Loading...</div> : null}
            <ul>
                {applications.map(application => (
                    <div>
                        <li>Position: {application.position}</li>
                        <li>Hourly Pay: {application.hourly_pay}</li>
                        <li>Date: {application.date}</li>
                    </div>
                ))}
            </ul>
        </div>
    )
}

export default Applications;
