import React, { useContext } from 'react';
import {
    Button
} from 'reactstrap'; 
import UserContext from '../routes/UserContext';
import { applyToJob } from '../utilities/utility';

const JobCard = ({ job }) => {
    const { currentUser } = useContext(UserContext);

    function formatDate(date) {
        let dateArr = date.split("-")
        let year = dateArr[0];
        let month = dateArr[1];
        let day = dateArr[2].substring(0,2);
        return `${month}/${day}/${year}`
    }

    const apply = async () => {
        const jobId = await applyToJob(currentUser, job.id);

    }

    return (
        <div>
            <p>Position: {job.position}</p>
            <p>Hourly pay: ${job.hourly_pay}</p>
            <p>Date: {formatDate(job.date)}</p>
            <Button onClick={apply} className="btn btn-success btn-sm">Apply</Button>
        </div> 
    )
}

export default JobCard;
