import React, { useState, useEffect } from 'react';
import ChefApi from '../api/api';
import { Button } from '@material-ui/core';
import { formatDate } from '../utilities/utility'; 

const CompaniesApplications = ({currentUser}) => {
    const [companies, setCompanies] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function getApplications() { 
            let applicationsArr = []
            const userCompanies = await ChefApi.getCompaniesFromUsername(currentUser);
            for (let company of userCompanies.companies) {
                const companyApplications = await ChefApi.getJobsForCompany(company.id)
                applicationsArr.push([company, companyApplications])
            }
            setCompanies(applicationsArr);
            console.log(companies)
            setIsLoading(false); 
        }
        if (isLoading && currentUser !== null) {
            getApplications()
        }
    }, [companies, currentUser, isLoading])


    const acceptApplicant = ([jobId, username]) => async (e) => {
        console.log(jobId, username)
    }

    const denyApplicant = ([jobId, username]) => (event) => {
        console.log(jobId, username)
    }

    return (
        !isLoading && companies.length > 0 ?
        <div>
            <h1>Current Applications</h1>
            <ul className="applications-ul">
                {companies.map(company => (
                    <div key={company[0].id}>
                        <li>Company: {company[0].name}</li>
                        <li>Address: {company[0].address}</li>
                        <li>URL: {company[0].url}</li>
                        <br></br>
                        {company[1].length > 0 ?
                        company[1].map(application => (
                            <ul key={application.id}>   
                                <li>Position: {application.position}</li>
                                <li>Hourly Pay: {application.hourly_pay}</li>
                                <li>Date: {formatDate(application.date)}</li><br></br>
                                {application.applications.length > 0 ?
                                    application.applications.map(applicant => (
                                    <ul key={applicant[0]}>
                                        <li>Username: {applicant[1]}</li>
                                        <li>Status: {applicant[2]}</li><br></br>
                                        <Button onClick={acceptApplicant([application.id, applicant[1]])} variant="contained" color="primary">Accept</Button>
                                        <Button onClick={denyApplicant([application.id, applicant[1]])} variant="contained" color="secondary">Deny</Button>
                                    </ul> 
                                    ))
                                : <p>No applicants</p> }
                            </ul>
                            ))
                         : <p>No applications to display</p>}
                    </div>
                ))}
            </ul>
        </div>
        : 
        <h1>Currently no applications to display</h1>
    )
}

export default CompaniesApplications;
