import ChefApi from '../api/api';


const editUser = async (currentUser, formData) => {
    try {
        const user = await ChefApi.editUser(currentUser, formData);
        return {success: true, user}
    }
    catch (err) {
        return {success: false, errors: err}
    }
}

const registerJob = async formData => {
    try {
        await ChefApi.signUpJob(formData)
        return {success: true}
    } catch (err) {
        return {success: false, errors: err}
    }
}

const registerCompany = async formData => {
    try {
        await ChefApi.signUpCompany(formData)
        return {success: true}
    } catch (err) {
        return {success: false, errors: err}
    }
}

module.exports = {
    editUser,
    registerCompany,
    registerJob
}
