const ChefApi = import('../api/api');

{    const SignUp = async formData => {
        try {
            const token = await ChefApi.Signup(formData)
            setCurrentUser(formData.username)
            setToken(token)
            localStorage.setItem('token', token);
            return {success: true}
        } catch (err) {
            return {success: false, errors: err}
        }
    }}

const registerJob = async formData => {
    try {
        await ChefApi.signUpJob(formData)
        return {success: true}
    } catch (err) {
        return {success: false, errors: err}
    }
}

const registerCompany = async formData => {
    try {
        await ChefApi.signUpCompany(formData)
        return {success: true}
    } catch (err) {
        return {success: false, errors: err}
    }
}


module.exports = {
    registerCompany,
    registerJob
}
