                console.log('res', res)
                const NEW_STATE = {
                    "name": res.company.name,
                    "address": res.company.address,
                    "url": res.company.url
                }
                setUserInfo(data => ({
                    ...data,
                    ...NEW_STATE
                }))
                alert("Updated company info")
            }
            else {
                setErrors(res.errors);
            }
        })
    }

    const handleChange = e => {
        const name = e.target.name;
        setFormData(data => ({
            ...data,
            [name]: value
        }))
    }

    return (
        <div>
        {formData === null  ? <p>Loading...</p> :
            <div>
                <h1>{name}</h1>
                <Form onSubmit={handleSubmit}>
                    <FormGroup>
                        <label id="name">Name: </label>
                        <input
                            type="text"
                            name="name"
                            onChange={handleChange}
                            value={formData.name}
                        />
                    </FormGroup>
                    <FormGroup>
                        <label id="url">URL: </label>
                        <input
                            type="text"
                            name="url"
                            onChange={handleChange}
                            value={formData.url}
                        />
                    </FormGroup>
                    <FormGroup>
                        <label id="email">Email:</label>
                        <input
                            type="email"
                            name="email"
                            onChange={handleChange}
                            value={formData.email}
                        />
                    </FormGroup>
                    <FormGroup>
                        <label id="isCompany">Are you a Business?: </label>
                        <input
                            type="checkbox"
                            name="isCompany"
                            onChange={handleChange}
                            value={formData.isCompany}
                            checked={!!formData.isCompany}
                        />
                    </FormGroup>
                    <Button>Edit Account</Button>
                    <div>
                        {errors.length ?
                            <p>{errors}</p>
                            : null }
                    </div>
                </Form>
                <a href="/userapplications"><Button>View Applications</Button></a>
            </div>
        }

            <h1>{name}</h1>
            <h3>{address}</h3>
            <h3>{url}</h3>
            <Button variant="contained" color="primary">Edit Company</Button>
        </div>

    )
}

export default EditCompany;
